import React, { useState, useEffect } from 'react';
import './Hero.css';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isVisible, setIsVisible] = useState(false);

  const heroSlides = [
    {
      title: "Shape Your Future in",
      highlight: "Fashion Design",
      subtitle: "Premier Fashion Education in Kerala",
      description: "Transform your creative vision into reality with our industry-leading fashion design programs. Join Kerala's most prestigious fashion institute.",
      image: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80",
      stats: { students: "5000+", years: "25+", placement: "95%" }
    },
    {
      title: "Master the Art of",
      highlight: "Textile Design",
      subtitle: "Innovation Meets Tradition",
      description: "Explore the rich heritage of textile arts while embracing cutting-edge design technologies. Create fabrics that tell stories.",
      image: "https://images.unsplash.com/photo-1586023492125-27b2c045efd7?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80",
      stats: { programs: "15+", faculty: "50+", awards: "100+" }
    },
    {
      title: "Launch Your Career in",
      highlight: "Fashion Business",
      subtitle: "Where Creativity Meets Commerce",
      description: "Learn the business side of fashion with our comprehensive merchandising and management programs. Build your fashion empire.",
      image: "https://images.unsplash.com/photo-1441984904996-e0b6ba687e04?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80",
      stats: { companies: "200+", internships: "500+", success: "98%" }
    }
  ];

  useEffect(() => {
    setIsVisible(true);
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 6000);

    return () => clearInterval(interval);
  }, [heroSlides.length]);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const currentSlideData = heroSlides[currentSlide];

  return (
    <section id="home" className="hero">
      {/* Background Slides */}
      <div className="hero-backgrounds">
        {heroSlides.map((slide, index) => (
          <div
            key={index}
            className={`hero-bg ${index === currentSlide ? 'active' : ''}`}
            style={{ backgroundImage: `url(${slide.image})` }}
          >
            <div className="hero-overlay"></div>
          </div>
        ))}
      </div>

      {/* Floating Shapes */}
      <div className="floating-shapes">
        <div className="floating-shape shape-1"></div>
        <div className="floating-shape shape-2"></div>
        <div className="floating-shape shape-3"></div>
        <div className="floating-shape shape-4"></div>
      </div>

      <div className="container">
        <div className="hero-content">
          <div className={`hero-text ${isVisible ? 'animate-fadeInUp' : ''}`}>
            {/* Badge */}
            <div className="hero-badge">
              <i className="fas fa-star"></i>
              <span>Kerala's #1 Fashion Institute</span>
            </div>

            {/* Main Title */}
            <h1 className="hero-title">
              {currentSlideData.title}
              <br />
              <span className="text-gradient">{currentSlideData.highlight}</span>
            </h1>

            {/* Subtitle */}
            <p className="hero-subtitle">{currentSlideData.subtitle}</p>

            {/* Description */}
            <p className="hero-description">{currentSlideData.description}</p>

            {/* Action Buttons */}
            <div className="hero-actions">
              <button className="btn btn-primary" onClick={() => scrollToSection('programs')}>
                <i className="fas fa-graduation-cap"></i>
                Explore Programs
              </button>
              <button className="btn btn-ghost" onClick={() => scrollToSection('about')}>
                <i className="fas fa-play"></i>
                Watch Video
              </button>
            </div>

            {/* Quick Stats */}
            <div className="hero-stats">
              {Object.entries(currentSlideData.stats).map(([key, value], index) => (
                <div key={key} className="stat-item" style={{ animationDelay: `${index * 0.2}s` }}>
                  <span className="stat-number">{value}</span>
                  <span className="stat-label">{key.charAt(0).toUpperCase() + key.slice(1)}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Hero Visual */}
          <div className={`hero-visual ${isVisible ? 'animate-fadeInRight' : ''}`}>
            <div className="hero-image-container">
              <div className="hero-image">
                <img src={currentSlideData.image} alt={currentSlideData.highlight} />
                <div className="image-overlay">
                  <div className="play-button" onClick={() => scrollToSection('gallery')}>
                    <i className="fas fa-play"></i>
                  </div>
                </div>
              </div>
              
              {/* Floating Cards */}
              <div className="floating-card card-1">
                <div className="card-icon">
                  <i className="fas fa-award"></i>
                </div>
                <div className="card-content">
                  <span className="card-title">Award Winning</span>
                  <span className="card-subtitle">Institution</span>
                </div>
              </div>
              
              <div className="floating-card card-2">
                <div className="card-icon">
                  <i className="fas fa-users"></i>
                </div>
                <div className="card-content">
                  <span className="card-title">Expert Faculty</span>
                  <span className="card-subtitle">Industry Leaders</span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Slide Indicators */}
        <div className="slide-indicators">
          {heroSlides.map((_, index) => (
            <button
              key={index}
              className={`indicator ${index === currentSlide ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
            >
              <span className="indicator-progress"></span>
            </button>
          ))}
        </div>

        {/* Scroll Indicator */}
        <div className="scroll-indicator" onClick={() => scrollToSection('about')}>
          <span>Discover More</span>
          <div className="scroll-arrow">
            <i className="fas fa-chevron-down"></i>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
