import React from 'react';
import './Hero.css';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Background image style
  const heroStyle = {
    backgroundImage: `linear-gradient(135deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.5) 100%), url(${process.env.PUBLIC_URL}/images/hero-main.jpg)`
  };

  return (
    <section id="home" className="hero with-background" style={heroStyle}>
      <div className="container">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Professional Fashion Education & Training Solutions
            </h1>
            <p className="hero-subtitle">
              Leading provider of comprehensive fashion design programs, industry certifications,
              and professional development services. Transform your career with our expert-led courses
              and cutting-edge curriculum.
            </p>
            <div className="hero-stats">
              <div className="stat-item">
                <span className="stat-number">500+</span>
                <span className="stat-label">Graduates Placed</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">15+</span>
                <span className="stat-label">Years Experience</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">95%</span>
                <span className="stat-label">Success Rate</span>
              </div>
            </div>
            <div className="hero-actions">
              <button
                className="btn btn-primary"
                onClick={() => scrollToSection('programs')}
              >
                View Our Services
              </button>
              <button
                className="btn btn-secondary"
                onClick={() => scrollToSection('contact')}
              >
                Request Consultation
              </button>
            </div>
          </div>
          <div className="hero-image">
            <div className="hero-card">
              <h3>Why Choose Oxford Fashion Institute?</h3>
              <ul className="feature-list">
                <li>Industry-certified instructors</li>
                <li>Hands-on practical training</li>
                <li>Career placement assistance</li>
                <li>Flexible learning schedules</li>
                <li>Modern facilities & equipment</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
