import React from 'react';
import './Hero.css';

const Hero = () => {
  // Navigation utility function
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Statistics data
  const stats = [
    { number: '500+', label: 'Graduates Placed' },
    { number: '15+', label: 'Years Experience' },
    { number: '95%', label: 'Success Rate' }
  ];

  // Feature list data
  const features = [
    'Industry-certified instructors',
    'Hands-on practical training',
    'Career placement assistance',
    'Flexible learning schedules',
    'Modern facilities & equipment'
  ];

  // Action buttons data
  const actions = [
    { 
      text: 'View Our Services', 
      className: 'btn btn-primary', 
      target: 'programs' 
    },
    { 
      text: 'Request Consultation', 
      className: 'btn btn-secondary', 
      target: 'contact' 
    }
  ];

  return (
    <section id="home" className="hero">
      <div className="container">
        <div className="hero-content">
          
          {/* Left Content Section */}
          <div className="hero-text">
            <div className="hero-header">
              <h1 className="hero-title">
                Professional Fashion Education & Training Solutions
              </h1>
              <p className="hero-subtitle">
                Leading provider of comprehensive fashion design programs, industry certifications, 
                and professional development services. Transform your career with our expert-led courses 
                and cutting-edge curriculum.
              </p>
            </div>

            <div className="hero-stats">
              {stats.map((stat, index) => (
                <div key={index} className="stat-item">
                  <span className="stat-number">{stat.number}</span>
                  <span className="stat-label">{stat.label}</span>
                </div>
              ))}
            </div>

            <div className="hero-actions">
              {actions.map((action, index) => (
                <button 
                  key={index}
                  className={action.className}
                  onClick={() => scrollToSection(action.target)}
                >
                  {action.text}
                </button>
              ))}
            </div>
          </div>

          {/* Right Content Section */}
          <div className="hero-image">
            <div className="hero-card">
              <div className="card-header">
                <h3>Why Choose Oxford Fashion Institute?</h3>
              </div>
              <div className="card-content">
                <ul className="feature-list">
                  {features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                  ))}
                </ul>
              </div>
            </div>
          </div>

        </div>
      </div>
    </section>
  );
};

export default Hero;
