import React, { useState, useEffect, useRef } from 'react';
import './Stats.css';

const Stats = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [counters, setCounters] = useState({
    students: 0,
    years: 0,
    placement: 0,
    programs: 0,
    faculty: 0,
    awards: 0
  });
  
  const sectionRef = useRef(null);

  const statsData = [
    {
      key: 'students',
      target: 5000,
      suffix: '+',
      label: 'Happy Students',
      icon: 'fas fa-user-graduate',
      color: '#667eea'
    },
    {
      key: 'years',
      target: 25,
      suffix: '+',
      label: 'Years Excellence',
      icon: 'fas fa-calendar-alt',
      color: '#764ba2'
    },
    {
      key: 'placement',
      target: 95,
      suffix: '%',
      label: 'Placement Rate',
      icon: 'fas fa-briefcase',
      color: '#f093fb'
    },
    {
      key: 'programs',
      target: 15,
      suffix: '+',
      label: 'Programs Offered',
      icon: 'fas fa-graduation-cap',
      color: '#4facfe'
    },
    {
      key: 'faculty',
      target: 50,
      suffix: '+',
      label: 'Expert Faculty',
      icon: 'fas fa-chalkboard-teacher',
      color: '#43e97b'
    },
    {
      key: 'awards',
      target: 100,
      suffix: '+',
      label: 'Awards Won',
      icon: 'fas fa-trophy',
      color: '#ffd700'
    }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !isVisible) {
          setIsVisible(true);
          animateCounters();
        }
      },
      { threshold: 0.3 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => observer.disconnect();
  }, [isVisible]);

  const animateCounters = () => {
    statsData.forEach((stat) => {
      let current = 0;
      const increment = stat.target / 100;
      const timer = setInterval(() => {
        current += increment;
        if (current >= stat.target) {
          current = stat.target;
          clearInterval(timer);
        }
        setCounters(prev => ({
          ...prev,
          [stat.key]: Math.floor(current)
        }));
      }, 20);
    });
  };

  return (
    <section className="stats-section" ref={sectionRef}>
      <div className="container">
        {/* Background Elements */}
        <div className="stats-bg-elements">
          <div className="bg-circle circle-1"></div>
          <div className="bg-circle circle-2"></div>
          <div className="bg-circle circle-3"></div>
        </div>

        {/* Section Header */}
        <div className={`stats-header ${isVisible ? 'animate-fadeInUp' : ''}`}>
          <div className="stats-badge">
            <i className="fas fa-chart-line"></i>
            <span>Our Achievements</span>
          </div>
          <h2>Numbers That Speak for <span className="text-gradient">Excellence</span></h2>
          <p>
            Over two decades of commitment to fashion education has resulted in remarkable achievements 
            and countless success stories that continue to inspire.
          </p>
        </div>

        {/* Stats Grid */}
        <div className={`stats-grid ${isVisible ? 'animate-fadeInUp' : ''}`}>
          {statsData.map((stat, index) => (
            <div 
              key={stat.key} 
              className="stat-card"
              style={{ 
                animationDelay: `${index * 0.1}s`,
                '--accent-color': stat.color 
              }}
            >
              <div className="stat-icon">
                <i className={stat.icon}></i>
              </div>
              <div className="stat-content">
                <div className="stat-number">
                  <span className="counter">{counters[stat.key]}</span>
                  <span className="suffix">{stat.suffix}</span>
                </div>
                <div className="stat-label">{stat.label}</div>
              </div>
              <div className="stat-decoration">
                <div className="decoration-line"></div>
                <div className="decoration-dot"></div>
              </div>
            </div>
          ))}
        </div>

        {/* Additional Info */}
        <div className={`stats-info ${isVisible ? 'animate-fadeInUp' : ''}`}>
          <div className="info-grid">
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-medal"></i>
              </div>
              <div className="info-content">
                <h4>Government Approved</h4>
                <p>Officially recognized by Government of Kerala with AICTE affiliation</p>
              </div>
            </div>
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-handshake"></i>
              </div>
              <div className="info-content">
                <h4>Industry Partners</h4>
                <p>Strong collaborations with 200+ leading fashion houses and brands</p>
              </div>
            </div>
            <div className="info-item">
              <div className="info-icon">
                <i className="fas fa-globe"></i>
              </div>
              <div className="info-content">
                <h4>Global Recognition</h4>
                <p>International accreditation and exchange programs worldwide</p>
              </div>
            </div>
          </div>
        </div>

        {/* Why Choose Oxford Fashion Institute */}
        <div className={`features-section ${isVisible ? 'animate-fadeInUp' : ''}`}>
          <div className="features-card">
            <h3>Why Choose Oxford Fashion Institute?</h3>
            <ul className="features-list">
              <li>Industry-certified instructors</li>
              <li>Hands-on practical training</li>
              <li>Career placement assistance</li>
              <li>Flexible learning schedules</li>
              <li>Modern facilities & equipment</li>
            </ul>
          </div>
        </div>

        {/* Success Stories Preview */}
        <div className={`success-preview ${isVisible ? 'animate-fadeInUp' : ''}`}>
          <div className="success-content">
            <h3>Success Stories</h3>
            <p>Our alumni work with top fashion brands globally</p>
            <div className="brand-logos">
              <div className="brand-logo">Zara</div>
              <div className="brand-logo">H&M</div>
              <div className="brand-logo">Nike</div>
              <div className="brand-logo">Adidas</div>
              <div className="brand-logo">Puma</div>
              <div className="brand-logo">Levi's</div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Stats;
