import React from 'react';
import './Hero.css';

const Hero = () => {
  // ===== DATA CONFIGURATION =====
  const heroData = {
    title: "KGCE FINE ARTS & ANIMATION",
    subtitle: {
      line1: "2 Year Poly Course",
      line2: "PSC Approved"
    },

    stats: [
      { number: "19+", label: "Years Experience" },
      { number: "4", label: "Courses" },
      { number: "2", label: "Centers" }
    ],

    actions: [
      { text: "View Our Services", style: "btn-primary", target: "programs" },
      { text: "Request Consultation", style: "btn-secondary", target: "contact" }
    ]
  };

  // ===== UTILITY FUNCTIONS =====
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // ===== STYLING =====
  // Modern fashionable girl background image with dark overlay for text readability
  const heroStyle = {
    backgroundImage: `linear-gradient(135deg, rgba(0, 0, 0, 0.9) 0%, rgba(0, 0, 0, 0.8) 100%), url(${process.env.PUBLIC_URL}/images/hero-main.jpg)`
  };

  // ===== COMPONENT RENDERS =====
  const renderStats = () => (
    <div className="hero-stats">
      {heroData.stats.map((stat, index) => (
        <div key={index} className="stat-item">
          <span className="stat-number">{stat.number}</span>
          <span className="stat-label">{stat.label}</span>
        </div>
      ))}
    </div>
  );

  const renderActions = () => (
    <div className="hero-actions">
      {heroData.actions.map((action, index) => (
        <button
          key={index}
          className={`btn ${action.style}`}
          onClick={() => scrollToSection(action.target)}
        >
          {action.text}
        </button>
      ))}
    </div>
  );

  const renderHeroContent = () => (
    <div className="hero-text">
      <div className="hero-header">
        <h1 className="hero-title">
          <span className="kgce-red">KGCE</span> FINE ARTS & ANIMATION
        </h1>
        <div className="hero-subtitle">
          <p className="subtitle-line">{heroData.subtitle.line1}</p>
          <p className="subtitle-line">{heroData.subtitle.line2}</p>
        </div>
      </div>
      {renderStats()}
      {renderActions()}
    </div>
  );

  // ===== MAIN RENDER =====
  return (
    <section id="home" className="hero with-background" style={heroStyle}>
      <div className="container">
        <div className="hero-content">
          {renderHeroContent()}
        </div>
      </div>
    </section>
  );
};

export default Hero;
