import React from 'react';
import './Hero.css';

const Hero = () => {
  // ===== DATA CONFIGURATION =====
  const heroData = {
    title: "Professional Fashion Education & Training Solutions",
    subtitle: "Leading provider of comprehensive fashion design programs, industry certifications, and professional development services. Transform your career with our expert-led courses and cutting-edge curriculum.",
    
    stats: [
      { number: "500+", label: "Graduates Placed" },
      { number: "15+", label: "Years Experience" },
      { number: "95%", label: "Success Rate" }
    ],
    
    actions: [
      { text: "View Our Services", style: "btn-primary", target: "programs" },
      { text: "Request Consultation", style: "btn-secondary", target: "contact" }
    ],
    
    features: {
      title: "Why Choose Oxford Fashion Institute?",
      items: [
        "Industry-certified instructors",
        "Hands-on practical training", 
        "Career placement assistance",
        "Flexible learning schedules",
        "Modern facilities & equipment"
      ]
    }
  };

  // ===== UTILITY FUNCTIONS =====
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // ===== STYLING =====
  const heroStyle = {
    backgroundImage: `linear-gradient(135deg, rgba(0, 0, 0, 0.9) 0%, rgba(0, 0, 0, 0.8) 100%), url(${process.env.PUBLIC_URL}/images/hero-main.jpg)`
  };

  // ===== COMPONENT RENDERS =====
  const renderStats = () => (
    <div className="hero-stats">
      {heroData.stats.map((stat, index) => (
        <div key={index} className="stat-item">
          <span className="stat-number">{stat.number}</span>
          <span className="stat-label">{stat.label}</span>
        </div>
      ))}
    </div>
  );

  const renderActions = () => (
    <div className="hero-actions">
      {heroData.actions.map((action, index) => (
        <button
          key={index}
          className={`btn ${action.style}`}
          onClick={() => scrollToSection(action.target)}
        >
          {action.text}
        </button>
      ))}
    </div>
  );

  const renderFeatureCard = () => (
    <div className="hero-card">
      <h3>{heroData.features.title}</h3>
      <ul className="feature-list">
        {heroData.features.items.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </ul>
    </div>
  );

  const renderHeroContent = () => (
    <div className="hero-text">
      <div className="hero-header">
        <h1 className="hero-title">{heroData.title}</h1>
        <p className="hero-subtitle">{heroData.subtitle}</p>
      </div>
      {renderStats()}
      {renderActions()}
    </div>
  );

  // ===== MAIN RENDER =====
  return (
    <section id="home" className="hero with-background" style={heroStyle}>
      <div className="container">
        <div className="hero-content">
          {renderHeroContent()}
          <div className="hero-image">
            {renderFeatureCard()}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
